shaders.vertex:
// default vertex shader

attribute vec4 aVertex;
attribute vec2 aTexCoord;
varying vec2 vTexCoord;

uniform float uT;
uniform vec2 uResolution;

void main(void)
{
  vTexCoord = aTexCoord;
  // float t = 2.0 * uT;
  // mat2 rot = mat2(cos(t), -sin(t), sin(t), cos(t));
  mat2 rot = mat2(1.0, 0.0, 1.0, 0.0);
  gl_Position = vec4(aVertex.xy, aVertex.z, 0.9);
}

shaders.fragment:
varying vec2 vTexCoord;
uniform sampler2D uTexture;
uniform vec2 uResolution;
uniform vec2 uMouse;
uniform float uMag; // 0.0, 1.0, 0.15
uniform float uR; // 0.0, 2.0, 0.34

void main(void) {
  vec2 mouse = uMouse / uResolution;
  vec2 xy = vTexCoord - mouse;
  float r = length(xy * vec2(uResolution.x / uResolution.y, 1.0));
  xy = r < uR ? xy * (uR - uMag) / sqrt(uR * uR - r * r) : xy;
  gl_FragColor = texture2D(uTexture, xy + mouse);
}
